import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import ConfirmPopup from "../components/ConfirmPopup";
import "../styles.css";

const ManagePets = () => {
  const [pets, setPets] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedPetId, setSelectedPetId] = useState(null);
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false);
  const [editPet, setEditPet] = useState(null);

  useEffect(() => {
    const fetchPets = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get("http://localhost:5134/petstores", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPets(response.data);
      } catch (error) {
        console.error("Error fetching pets:", error);
      }
    };

    fetchPets();
  }, []);

  const handleDeleteClick = (petId) => {
    setSelectedPetId(petId);
    setIsPopupOpen(true);
  };

  const handleConfirmDelete = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.delete(`http://localhost:5134/petstores/${selectedPetId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Update the list of pets after deletion
      setPets((prevPets) => prevPets.filter((pet) => pet.id !== selectedPetId));
    } catch (error) {
      console.error("Error deleting pet:", error);
    }
    setIsPopupOpen(false); // Close the popup after deletion
  };

  const handleCancelDelete = () => {
    setIsPopupOpen(false);
    setSelectedPetId(null);
  };

  const handleEditClick = (pet) => {
    setEditPet(pet);
    setIsEditPopupOpen(true);
  };

  const handleSaveEdit = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.put(`http://localhost:5134/petstores/${editPet.id}`, editPet, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPets((prevPets) =>
        prevPets.map((pet) => (pet.id === editPet.id ? editPet : pet))
      );
    } catch (error) {
      console.error("Error updating pet:", error);
    }
    setIsEditPopupOpen(false);
    setEditPet(null);
  };

  const handleCancelEdit = () => {
    setIsEditPopupOpen(false);
    setEditPet(null);
  };

  return (
    <div>
      <div>
        <Link to="/" className="back-to-dashboard">
          ← Back to Dashboard
        </Link>
      </div>
      <h2>Manage Pets</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Pet Name</th>
            <th>Gender</th>
            <th>Description</th>
            <th>Price</th>
            <th>Birth Date</th>
            <th>Image</th>
            <th style={{ width: "150px" }}>Category</th>
            <th style={{ width: "150px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pets.map((pet) => (
            <tr key={pet.id}>
              <td>{pet.id}</td>
              <td>{pet.petName}</td>
              <td>{pet.gender}</td>
              <td>{pet.petDescription}</td>
              <td>${pet.price.toLocaleString()}</td>
              <td>{new Date(pet.birthDay).toLocaleDateString()}</td>
              <td>
                <img
                  src={pet.imageUrl ? `http://localhost:5135/${pet.imageUrl}` : "/placeholder-image.png"} // Ensure this matches the property name in your database
                  alt={pet.petName}
                  style={{ width: "100px", height: "auto" }}
                  onError={(e) => {
                    console.error(`Image load error for ${pet.petName}: ${pet.imageUrl}`);
                    e.target.src = "Image is not found";
                  }}
                  onLoad={() => {
                    console.log(`Image loaded for ${pet.petName}: ${pet.imageUrl}`);
                  }}
                />
              </td>
              <td style={{ width: "150px" }}>{pet.categoryName ? pet.categoryName : "No Category"}</td>
              <td style={{ width: "150px" }}>
                <button onClick={() => handleEditClick(pet)}>Edit</button>
                <button onClick={() => handleDeleteClick(pet.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {isPopupOpen && (
        <ConfirmPopup
          message="Are you sure you want to delete this pet?"
          onConfirm={handleConfirmDelete}
          onCancel={handleCancelDelete}
        />
      )}
      {isEditPopupOpen && (
        <ConfirmPopup
          message="Edit Pet Details"
          onConfirm={handleSaveEdit}
          onCancel={handleCancelEdit}
        >
          <div>
            <label>
              Pet Name:
              <input
                type="text"
                value={editPet.petName}
                onChange={(e) =>
                  setEditPet({ ...editPet, petName: e.target.value })
                }
              />
            </label>
            <label>
              Description:
              <input
                type="text"
                value={editPet.petDescription}
                onChange={(e) =>
                  setEditPet({ ...editPet, petDescription: e.target.value })
                }
              />
            </label>
            <label>
              Price:
              <input
                type="number"
                value={editPet.price}
                onChange={(e) =>
                  setEditPet({ ...editPet, price: parseFloat(e.target.value) })
                }
              />
            </label>
            <label>
              Birth Date:
              <input
                type="date"
                value={new Date(editPet.birthDay).toISOString().split("T")[0]}
                onChange={(e) =>
                  setEditPet({ ...editPet, birthDay: e.target.value })
                }
              />
            </label>
            <label>
              Category:
              <input
                type="text"
                value={editPet.categoryName}
                onChange={(e) =>
                  setEditPet({ ...editPet, categoryName: e.target.value })
                }
              />
            </label>
          </div>
        </ConfirmPopup>
      )}
    </div>
  );
};

export default ManagePets;
