import React, { useState } from "react";
import axios from "axios"; // Import axios for making HTTP requests
import "../styles/EditPopup.css"; // Import the CSS for the popup

const EditPopup = ({ pet: initialPet, onSave, onCancel, onChange }) => {
  const [pet, setPet] = useState(initialPet);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("petId", pet.id); // Add petId to the form data
      try {
        const response = await axios.post("http://localhost:5134/petStores/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        const imageUrl = response.data.imageUrl; // Use the full image URL as returned by the server
        onChange({ ...pet, imageUrl: imageUrl });
      } catch (error) {
        console.error("Error uploading image:", error);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPet((prevPet) => ({
      ...prevPet,
      [name]: name === "birthDay" ? new Date(value).toISOString() : value,
    }));
  };

  return (
    <div className="popup">
      <div className="popup-inner">
        <h2>{pet.id ? "Edit Pet Details" : "Add Pet Details"}</h2>
        <label>
          Category:
          <input
            type="text"
            value={pet.categoryName}
            onChange={(e) => onChange({ ...pet, categoryName: e.target.value })}
            disabled={!!pet.id} // Disable input if pet has an ID (editing)
            style={{ backgroundColor: pet.id ? "#d3d3d3" : "white" }} // Gray out when editing
          />
        </label>
        <label>
          Pet Name:
          <input
            type="text"
            value={pet.petName}
            onChange={(e) => onChange({ ...pet, petName: e.target.value })}
          />
        </label>
        <label>
          Gender:
          <input
            type="text"
            value={pet.gender}
            onChange={(e) => onChange({ ...pet, gender: e.target.value })}
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            value={pet.petDescription}
            onChange={(e) => onChange({ ...pet, petDescription: e.target.value })}
          />
        </label>
        <label>
          Price:
          <input
            type="number"
            value={pet.price}
            onChange={(e) => onChange({ ...pet, price: parseFloat(e.target.value) })}
          />
        </label>
        <label>
          Birth Date:
          <input
            type="date"
            name="birthDay"
            value={pet.birthDay ? new Date(pet.birthDay).toISOString().split('T')[0] : ''} // Ensure valid date format
            onChange={handleInputChange}
          />
        </label>
        <label>
          Image:
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </label>
        <button onClick={onSave}>Save</button>
        <button onClick={onCancel}>Cancel</button>
      </div>
    </div>
  );
};

export default EditPopup;
